    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(32734,"The Cobra Strikes", 2575,"The Torch and Toro spot the kidnapping of the South American Ambassador but they are gassed by the perpetrators. With Toro still out and maybe dead the Torch chases the criminals into the lair of the Cobra to rescue the Ambassador from the clutches of the madman and his gang.",NULL,"The Masterworks edition lists writer as unknown pencils by Mike Sekowsky & unknown inker unknown.",19
