    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(32735,"Satan and the Sorcerer's Secret!", 2575,"Balthar comes to Bald Mountain in order to study Egyptian writings on how the souls of the dead can be brought back to life when Satan appears and claims the sorcerer's soul until he can redeem it. How? Bring back the world's greatest champion so that the Prince of Evil can defeat him.....and that man is Captain America!",NULL,"The Masterworks edition lists the writer as unknown pencilers as Pierce Rice & unknown and the inkers as unknown & George Klein.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(32736,"Catch That Firebug!", 2575,"Young Mickey Forrest believes that he can decipher a typewritten code that even top police can't decipher and when he does he uncovers the plans of a known arsonist out to set fire to a warehouse.",NULL,NULL,21
