    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(33269,"Hitler Is a Bum!", 2633,"While in Germany the members of the Ghost Patrol fight to save the \"freedom of the press\" in that nation by challenging the Fhrer himself!",NULL,NULL,19
