    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(33509,"The Sabotage of the Supply Trains", 2660,"To mend damaged U.S.-Russian relations the Torch and Toro investigate the disappearance of ammunition from crates shipped to Russia either before being shipped or switched while in transit.",NULL,"The Masterworks edition lists the writer as unknown penciler as Harry Fisk and inker as Edd Asch.",19
