    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(49241,"The Forgotten Crime!", 4439,NULL,NULL,"Cover art credits from Craig Delich. Martin Naydel drew the Flash and Green Lantern figures which were pasted over the Spectre and Starman figures on this cover.The cover carries the AA Bullet for All-American Comics Inc..",6
