    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(49256,"The Butler's Apprentice!", 4440,"A group of criminals have targeted a rich place to rob but it is so well guarded that they find it difficult to get in the place. Then they get an idea: one of them will be trained as a butler who'll work as an inside man.....and Alfred is chosen as the one to train him!",NULL,"Art credits verified by Jerry Robinson. Letterer credit verified by George Roussos.",19
