    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(49273,"Shenanigans in Sherwood Forest", 4442,"After a particularly hard day of catching criminals the Flash decides to travel into the past in order to get away from crime and take a well-deserved rest. Unfortunately he picks Sherwood Forest at the time of Robin Hood and is mistaken for one of Robin's men!",NULL,"Changes to sequencing by Len Wolinsky 2006-3-15.",19
