    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(50322,"The Mystery of the Metal Menace!", 4588,NULL,NULL,"Art credits from Craig Delich.This was the last issue of All-Star that carried the AA (All-American Comics) bullet on the cover.",6
