    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(54264,"The Man Who Knows Too Much!", 5105,NULL,NULL,"The cover art is repeated on the splash page. The head of Wonder Woman is either a tracing or photostat of artwork by Harry G. Peter.",6
