    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(56067,"The Phony Death-House Reverend", 5332,"Lippy helps Monk escape from prison and immediately Monk attempts a job at a bank. He loses a foot during the escape and his mob members decide to abandon him for which Monk decides to rub the whole gang out. Only Daredevil and the Little Wise Guys can stop him.",NULL,NULL,19
