    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(56339,"The Perfect Hideaway!", 5364,"A youngster has been swindled out of a thousand dollars and given supposed ownership of a bridge. Henry discovers the lad seeking to collect a toll for people using the bridge informs him that the deed is a forgery and sets out to round up the foul felon then discovers the crook has swindled others as well.",NULL,"All dialogue is in rhyme.",19
