    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58443,"Mickey Mouse and the Beanstalk", 5661,NULL,NULL,"Cover blurb says 'From the motion picture \"Fun and Fancy Free\".'",6
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58444,"Mickey and the Beanstalk", 5661,"Famine strikes Happy Valley when the Golden Harp is stolen. After Mickey trades his cow for some magic beans a giant beanstalk grows into the sky taking Mickey Goofy and Donald's house with it. In the land in the sky the three go to the giant's castle and rescue the Golden Harp.",NULL,"Adapted from Mickey and the Beanstalk in the animated feature Fun and Fancy Free (1947).",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58445,"Pluto Keeps It Quiet", 5661,"Mickey gets a job as a night watchman and Pluto tries to keep the house completely quiet when Mickey sleeps during the day.",NULL,"Story continues on inside back cover in black red and white and concludes on the back cover in color.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58446,"The Ghost of The Grotto", 5662,NULL,NULL,NULL,6
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58447,"Donald Duck and the Ghost of the Grotto", 5662,"While the ducks are running a kelp-gathering boat in the West Indies they are told of the mystery of an armored man who appears every fifty years to kidnap a small boy. This time the armored man kidnaps Dewey and the ducks fight to free him from the armored man's stronghold in a grotto in a coral reef beneath a rotten old galleon guarded by an enormous octopus. ",NULL,"Story title is slightly different than cover title. Story begins on the inside front cover in black red and white. Art submitted on April 15 1947. Synopsis by Michael Barrier from \"Carl Barks and the Art of the Comic Book\" (1982). ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58448,"Adventure Down Under", 5662,"Donald is hypnotized into believing he is a kangaroo and buys airplane tickets to Australia for himself and the nephews. In Australia the ducks try to trap Mournful Mary the \"queen of the kangaroos\" and this leads to Donald's falling into the hands of savage aborigines.",NULL,"Story continues on inside back cover in black red and white and concludes on the back cover in color. Art submitted on April 4 1947. Synopsis by Michael Barrier from \"Carl Barks and the Art of the Comic Book\" (1982).",19
    );
