    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58451,"Roy Rogers and the Guns of Mystery Mountain", 5663,"Roy Rogers Kay Roberts a rancher's daughter and Larry Parsons the ranch's foreman deal with cattle rustlers who operate out of an abandoned mine.",NULL,"Script submitted by Gaylord Du Bois on September 9 1946. Albert Micale art credit for this sequence from Alberto Becattini (May 14 2007). Robert Phillips said possible Erwin Hess and/or Jesse Marsh contribution on pencils and inks.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58452,"Roy and Dale on Trigger", 5663,"Pre-1947 posed-for-comics (as if film still) photo shows Roy and Dale on original Trigger in an action scene. Facsimile autograph. ",NULL,"Inside back cover; black and white. ",14
