    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58455,"Tarzan's Friends", 5664,"Descriptions of Tarzan's animal friends.",NULL,"inside front cover; black red and white. Text and illustrations.",20
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58456,"Tarzan and the Fires of Tohr", 5664,"Tarzan and his friend Paul D'Arnot find Professor James Barton and his daughter Ruth alone in the jungle after their bearers had deserted them. The quartet is attacked by hostile natives and have to flee by canoe. A swift and unexplored river sends the canoe through a long rock tunnel and when they emerge they find themselves in the land of Queen Ahtea and the lost city of Tohr. The Queen decides that Tarzan would make a good husband and King. When Tarzan does not agree with her plans he and his friends must escape from the Queen's dungeons.",NULL,"Story is complete but setting is continued in Tarzan (Dell 1948 series) #1 (January-February 1948).",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58457,"Tarzan and the Black Panther", 5664,"Tarzan and his friend Muviro battle the Arab slave trader Sidi Ben Yemlik known as the Black Panther.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58458,"Jungle Animals", 5664,"List of Tarzan's names for the jungle animals: Numa = lion; Pacco = zebra; Sheeta = leopard; Duro = hippopotamus; Dango = Hyena; Gimla = crocodile.",NULL,"Inside back cover; black red and white. Text and illustrations.",20
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58459,"Tarzan Holds Man Aloft", 5664,"Tarzan holds a white hunter over his head. Battle scene from \"Tarzan and the Black Panther.\"",NULL,"Back cover. Full-page illustration. ",14
