    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58461,"Weight and Fortune Machine", 5665,"Lulu gets a card from a weight and fortune machine. She likes the fortune but is unhappy with the weight.",NULL,"Inside front cover; black red and white. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58462,"The Case of the Purloined Popover", 5665,"Lulu is spanked by her mother who thinks she stole some popovers from the kitchen. Lulu tells the story to Tubby who immediately says her father is the culprit. Tubby with a mustache from his Junior G-Man kit goes to Lulu's house to conduct an investigation.",NULL,"This is the same format as the later \"The Spider Spins Again\" stories featuring Tubby but without Tubby calling himself \"The Spider.\" ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58463,"Intercepted Flowers", 5665,"Tubby buys some flowers to give to Lulu. Another girl sees Tubby and thinks the flowers are for her. ",NULL,"Pantomime story. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58464,"Alvin's Solo Flight", 5665,"Lulu and Tubby go to the beach but have to take Alvin along with them. Alvin manages to get lost at the beach and Lulu and Tubby try to find him.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58465,"Too Vivid Imagination", 5665,"Tubby stands at a bakery window and imagines eating all the goods on display. He then goes to the window of a soda shop and imagines having six sodas.  All the imaginary eating gives him a stomachache. ",NULL,"Pantomime story. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58466,"Unwanted Attention", 5665,"Alvin sprays himself with some of his mother's perfume but discovers to his horror that the aroma attracts little girls. ",NULL,"Pantomime story. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58467,"Never Again", 5665,"Lulu borrows her father's pipe smokes some doll hair and has hallucinations. ",NULL,"No dialogue other than three question mark balloons. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58468,"Turnabout", 5665,"Alvin squirts a girls with his squirt gun causing her to cry. Ashamed he squirts himself with the gun which make the girl laugh.",NULL,"Pantomime story.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58469,"Wild West Violin Practice", 5665,"Tubby practices his violin while reading a wild west magazine. ",NULL,"inside back cover; black red and white. Pantomime story. ",19
