    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58470,"Radio Violence", 5665,"Lulu listens happily to a violent radio program but is upset when the villain on the show kicks a dog. ",NULL,"Back cover; pantomime story. ",19
