    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58474,"Roy with Saddle and Rifle", 5666,"Photo posed-for-comics shows Roy in action scene as if film still lying over saddle rifle in hand. Facsimile signed \"Roy Rogers.\"",NULL,"Inside back cover; black and white.",14
