    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58478,"Microbe Battle or \"A New Champ of the Slap Swappers\"", 5667,"Mr. K. O. Biffer the fight promoter tricks Popeye into signing a contract for Swee'pea to fight Baby Boom for the \"Microbeweight\" title of the world.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58479,"Water Hole or \"Kin Ya Spare a Couple of Thousand Fer a Cup of Water?\"", 5667,"A water hole is found on Olive's desert property and Popeye and Olive go to Slither Creek. They sell the property to Mayor Gunrod not realizing that the water hole is the only source of water for the area.  Disgusted when Gunrod starts selling water for $100 a cup Popeye starts digging a well in the middle of the only street in town. ",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58480,"Fish Bait", 5667,"Popeye goes on a fishing trip with Prof. Herman H. Haddock and Dr. G. B. Bluzard who are fighting over the honor being the first to catch a rare Roccus Scomberomorus Lineatus (commonly called a \"Flip Fish.\")",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58481,"Haw haw!  Here comes a little kid!", 5667,"A man tries to play a practical joke on Swee'pea but has the tables turned on him. ",NULL,"inside back cover; black red and white. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58482,"Swee'Pea and Ball", 5667,"Swee'pea is throwing a ball in the air and catching it until Popeye interrupts him. ",NULL,"Back cover.",19
