    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58484,"Realistic Fishing", 5668,"Morty and Ferdie help Mickey catch fish when he and Minnie are fishing off a pier. ",NULL,"Inside front cover; black white. and red. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58485,"Mickey Mouse on Spook's Island", 5668,NULL,NULL,"Mickey and Goofy go undercover to investigate the disappearance of ten sailors all with criminal records on five voyages of the \"Lucy Belle.\" After signing up with the Lucy Belle they find that the captain is in league with Black Pete. They escape in a boat to a strange island where they are captured and become part of a human zoo filled with the missing sailors. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58486,"Mickey Mouse's Masquerade", 5668,"Morty and Ferdie think Mickey is broke when he dresses in rags for Minnie's \"ragamuffin party.\" They try to sell Pluto and get involved in tracking a bank robber.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58487,"Helpful Goofy", 5668,"A thug mistakes Mickey for \"Roscoe Rodent\" and attempts to collect a debt. ",NULL,"Inside back cover; black red and white. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58488,"Unhelpful Umbrella", 5668,"Mickey carries an umbrella on an apparently sunny day but gets soaked by a balky water fountain. ",NULL,"Back cover. ",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58489,"Adventure in Opto", 5669,NULL,NULL,NULL,6
