    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58497,"The Broken Heel", 5670,"Tillie breaks a heel on her shoe. She and Mac get in trouble when Mac breaks the window on the door of a shoe repair shop. ",NULL,"Back cover.",19
