    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58505,"Christmas on Bear Mountain", 5672,"Donald's stingy old Uncle Scrooge McDuck lets the ducks use his mountain cabin for the Christmas holidays planning to test Donald's bravery by coming to the cabin disguised as a bear. However two real bears--a mother and her cub--turn up at the cabin soon after the ducks' arrival.",NULL,"Art submitted on July 22 1947. First appearance of Uncle Scrooge. Synopsis by Michael Barrier from \"Carl Barks and the Art of the Comic Book\" (1982).",19
