    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58506,"Santa Claus Land", 5672,"Mickey and Goofy are working at Santa's Workshop at the North Pole. Doc and Grumpy of the Seven Dwarfs and Dumbo are also working there. Goofy is made custodian of Santa's reindeer where he discovers that Prancer has been stolen by Willie the Giant. ",NULL,"Letters credit for this sequence from Alberto Becattini (May 14 2007).",19
