    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(58510,"The Thinker Cooks with Gas!", 5673,"Fearing the Flash but respecting him at the same time the Thinker devises the one escape-proof trap that he feels that will finally eliminate the Scarlet Speedster for all time. The Fastest Man Alive however uses the Thinker's own weapon against him!",NULL,"Script credit from Craig Delich.",19
