    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(62255,"The Injustice Society of the World!  (introduction)", 6172,"The JSA attends a radio program that will honor them for their deeds the announcer suddenly praises the Injustice Society and open fires on the group. The Atom punches him through a window....but he mysteriously disappears leaving behind only bits of straw. The JSA hold an emergency meeting and hear over radio about jail breaks all over the country and a paper left behind by the announcer declares all-out war on the forces of law and order by the Injustice Society.The JSA accepts the challenge and splits up to cover the jail breaks while the Injustce Society set out to stop the JSA.",NULL,"Inker credit from Craig Delich. The splash page replicates the cover.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(62256,NULL, 6172,NULL,NULL,"This filler was NOT reprinted in the Archive.Letterer credit by Craig Delich.",5
