    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(62262,"The Injustice Society of the World!  (Chapter 4)", 6172,"On the eastern seaboard the Atom arrives at the HQ of General \"Stubby\" Klemper notifying him that Wonder Woman has informed him that lifelike automations are being used by the Injustice Society to impersonate important government officials. Then he discovers the \"General\" to be one and disables it and disguises himself like the General. Using the automation's control box he follows the signal to the HQ of the Injustice Society frees the General and attacks the villains but is captured.",NULL,NULL,19
    );
