    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(63354,"History's Crime Wave!  (Part 1)", 6312,"The Police are stunned and mystified with five separate crimes each resulting in a death and each being blamed on (by the suspects) by infamous villain in history. All the suspects are quickly tried convicted and sentenced to death. To make matters worse when six members of the JSA investigate they are actually killed by the villains blamed for the other crimes! A dying Johnny Thunder sends his companion the Black Canary to JSA HQ to report all that has gone on to Wonder Woman.",NULL,"The splash page replicates the cover.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(63355,"History's Crime Wave!  (Part 2)", 6312,"As Wonder Woman sits alone in JSA HQ wondering what has happened to the All-Stars Black Canary enters and tells the Amazon Maiden what has happened to Johnny. Wonder Woman leaves and collects the corpses of the JSA members and takes them back to HQ. Then she and the Canary fly them to Paula's secret lab and bring them back to life again with the Purple Ray after which the members dash to the wax museum looking for the villains. Realizing they are only wax they are perplexed. However Black Canary after listening to GL's incident figures out a way to capture the true villain.",NULL,"Art credits from Craig Delich. Page count correction by Craig Delich.This is the only JSA story in which Paula appears and the first JSA tale to feature the Invisible Plane.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(63356,NULL, 6312,NULL,NULL,"This filler was NOT reprinted in the Archive.Letterer credit by Craig Delich.",5
