    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(66138,NULL, 6698,"Roy Rogers subscriptions. 2 years 24 issues $1.75. 1 year 12 issues $1.00.",NULL,"Cover #2. Inside front cover: Subscription ad page with encircled Roy and \"Trigger\" B&W photo.",16
