    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(70921,"The Seven Dwarfs and the Pirate", 7368,"Yellow-Beak the parrot comes to the Dwarfs' house with a map of buried pirate treasure and convinces the Dwarfs to help him find it. The Dwarfs and Yellow-Beak set sail on a ship but the Witch changes herself into a rat to hitch a ride to the isle where the treasure is buried. ",NULL,"Script credit for this sequence from Alberto Becattini (May 14 2007).",19
