    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(70937,"Dumbo and Little Pedro", 7372,"Pedro the mail plane runs out of gas. Dumbo takes the mail and is warned to watch out for the witch who is always trying to rob the mail planes. ",NULL,"The character of Pedro the mail plane is from the 1947 Disney movie \"Fun and Fancy Free.\"",19
