    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(72365,"Steady Boy Friends", 7575,"High school student Sue wants to go steady with Frankie but her mother says she's too young.  Sue visits Nancy Hale for advice and meets Mrs. M who says she went steady with a boy when she was young.  However when they broke up she was very lonely because everyone thought she was \"taken.\"  Sue decides to wait before going steady with anyone.",NULL,NULL,19
