    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(74611,"Fury of the Golden Apes!", 7899,"Gus Corrigan seeking his lost brother asks Kaänga to help him.  Unknown to Corrigan his brother has been captured by a gorilla cult and the leader of the cult has given him amnesia-inducing drugs and convinced him that he is the gorilla god Jebola returned to his people.  Kaänga locates the cultists and frees Corrigan's brother and turns the cult leader over to the district commissioner.",NULL,"The story title is taken from the cover blurb.first line:  \"'Beware the flying furies -- for they are the messengers of the golden ape!'\"",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(74612,"Many hunters invade the privacy of the kingdom of Simba king of the beasts!", 7899,"Simba is challenged by Crekko for the kingship of the jungle.  While they are fighting a big game hunter spies them and fires his rifle wounding Simba.  Simba is taken captive by a wildlife photographer who wants to use footage of the lion in his new film.  While Simba is captive Crekko declares himself king of the jungle.  Simba escapes his captors and attacks and kills Crekko an attack that is captured on film by the happy photographer.",NULL,NULL,19
    );
