    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(75770,"The Veldt of Phantom Fangs", 8086,"The witch Zan-zar begins preaching war among the jungle natives.  An old woman who gives her name as Barda takes Kaänga and Ann to Zan-zar's hidden headquarters.  Barda reveals that she is the real Zan-zar and the leopard cult that had been serving the imposter turns on her",NULL,"The story title is taken from the cover blurb.first line:  \"Heed my commands...\"",19
