    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(75772,"Each year Fort Diablo's lancer corps recruited their men from the jungle tribes.", 8086,"The crooked trader Congo Jacques uses ventriloquism to convince natives that their god wishes the lancers driven from the jungle.  The natives attend Terry's annual recruitment drive and wreak havok.  Terry exposes Jacques and wins over all the recruits.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(75773,"Sleeping safely in his tree home Wambi the jungle boy stirs restlessly...", 8086,"Offended that his people would prefer Western medicine to his Kaw poisons the jungle's waterholes driving the animals mad.  Wambi forces Kaw to treat the animals and promise to never poison them again.",NULL,NULL,19
