    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(75953,"Donald Duck and \"The Pixilated Parrot\"", 8114,"Donald gives Uncle Scrooge a parrot that only counts instead of talking and Scrooge has the parrot memorize the combination to his safe; which Scrooge has been forgetting. However the parrot first gives the combination to two burglars and then flies away to a boat bound for Latin America.",NULL,"Story submitted February 23 1950. Synopsis by Michael Barrier from \"Carl Barks and the Art of the Comic Book\" (1982).",19
