    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(76995,"Wambi and the Reign of Jungle Terror", 8255,"Chief Ubali and his people start hunting in Wambi's jungle for ivory and hides to trade.  They capture Wambi but a great storm arises and Tawn manages to free Wambi under cover of the rain.  Wambi summons all the jungle animals and they attack Ubali and his people and Wambi extracts a promise from Ubali to never hunt in Wambi's jungle again.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(76996,"At each native village on the twisting trails safaris halted to dismiss their old guides...", 8255,"Camilla is deceived into attacking an ivory caravan slowing it down for thieves to arrive and take the ivory.  On learning the truth Camilla attacks the thieves and returns the ivory to the Harmons who are taking it the coast to sell.",NULL,NULL,19
