    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(77850,"Under the Big Top [The Circus of a Thousand Thrills!]", 8372,NULL,NULL,"The original art to this cover with new title lettering (to replace that which had fallen off) became the back cover to America vs. the Justice Society #4 (April 1985).",6
