    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78305,"Ghost Ship", 8434,"A man and his wife are flying in a small plane when a thick fog arises and they are forced to put down at sea. Using a lifeboat for a couple of days they come across a rotted ship. Reading the ship's log they learn the mutinous crew died one by one after being cursed by the captain they forced to walk the plank. The man sees a ship heading towards them and are afraid they are going to be rammed but the ship passes right through where they are standing! They get in their lifeboat and are rescued but the captain does not believe their strange tale of the ghost ship.",NULL,NULL,19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78306,"The Hungry Grave", 8434,"A woman hates her husband so she gives him small doses of arsenic trying to work up her nerve to finish him off but all she succeeds in doing is allowing him to develop a tolerance to the stuff. When he overhears her plot to leave him in a sack for her lover to bury in a dug-up grave he poisons her instead and puts her in the sack so that the lover buries her not quite dead. Then he kills the lover with an ax.",NULL,"Script credit from \"Tales of Terror! -- The EC Companion\" by Fred von Bernewitz and Grant Geissman.  Original indexer credited Al Feldstein.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78307,"Cave Man", 8434,"A man thaws out after 200000 years in ice and attacks his exhibitors.",NULL,"Script credit from \"Tales of Terror! -- The EC Companion\" by Fred von Bernewitz and Grant Geissman.  Original indexer credited Al Feldstein.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78308,"Incident On the Cliff", 8434,NULL,NULL,"NOT reprinted in Tales From the Crypt (Russ Cochran 1992 series) #3 (March 1993).",21
