    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78650,"Double Indemnity!", 8477,"A man kills his wife after taking out a double-indemnity policy on her then attempts to make her death look like an accident. But fate has a twist in store for him.",NULL,NULL,21
