    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(78651,"High Tide!", 8477,"Four men on a mail boat leave the infamous Mephisto Prison and soon hear by radio of the escape of one of the prisoners. When the boat's engine clogged with sand brings the craft to a halt a ways off shore all fingers begin to point to each other as the culprit.",NULL,NULL,19
