    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79344,"The Last City", 8568,"Concerned with possible annihilation by the Atomic Bomb a scientist invents a machine capable of creating a protective shell around the Earth making it impervious to atomic destruction. However when the machine also creates a protective bubble around itself the machine cannot be turned off with devastating results.",NULL,"Story inspired by \"The Lanson Screen\" by Arthur L. Zagat.Script addition of Gaines by Craig Delich.",19
