    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79832,"The Wonder Woman Story", 8661,"A female reporter gets the scoop that will win her a job at the Daily Globe over her male rival-- the secret story of Wonder Woman and the Amazons.",NULL,"The origin of Wonder Woman and the Amazons is retold although the creation of the Amazons from clay as detailed in Wonder Woman (DC 1942 series) #1 is omitted.Mary watches the origin on the Space-and-Time Transformer Screen which seems to have taken the place of the Magic Sphere.  The Space-and-Time Transformer is primarily used to send Wonder Woman anywhere on the planet at any time past or future.Wonder Woman was herself hired as the Romance Editor at the Daily Globe in Sensation Comics (DC 1942 series) #97.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79833,"The Amazon and the Leprechaun", 8661,"A leprechaun wins a scholarship to Holliday College and Wonder Woman as acting dean must endure his pranks while protecting him from gangsters attempting to steal his pot of gold.",NULL,NULL,19
