    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79834,"Helene Kottauer", 8661,"Story of Helene Kottauer (1410-1470) who made a perilous journey to crown the son of the dead Emperor of Austria and whose records of court life were cherished for centuries.",NULL,"Writer and inker id by Craig Delich 9/05. Editing and revised pencil credits by Jerry Bails (October 2005). Pencil credit previously given to John Giunta.",19
