    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79878,"Dead-Ringer", 8667,"A petty thief learns that his virtual double a millionaire is an amnesia victim residing at a local sanitarium and plans to somehow get into the sanitarium dispose of the rich man and take his place then suddenly come out of his amnesia state and assume the man's identity. But all is not as rosy as the man supposes......and pays for it with his life!","in E.C. Archives: Crime SuspenStories (Gemstone 2007 series) #1 (September 2007)","Cover story.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79879,"A Moment of Madness!", 8667,"A noted brain surgeon is experiencing blackouts which cause him to unconsciously commit acts of violence during his blackouts.","in E.C. Archives: Crime SuspenStories (Gemstone 2007 series) #1 (September 2007)",NULL,19
