    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79901,"To---?", 8670,"A ship heading for Jupiter experiences gyro problems passes by Jupiter and is marooned in space with no contact in the entire solar system.",NULL,NULL,21
