    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79902,"Deadlock!", 8670,"Two years in outer space and the Earth crew spots a strange-looking craft leerily approaching it. They communicate via an automatic brain machine and their mistrust of one another's intentions becomes clear so they end up destroying each other.",NULL,"Source for plot credit is “Against the Grain” (Stewart 2003 page 40).Story inspired by \"First Contact\" by Murray Leinster.Script revision from Al Feldstein to Wood by Craig Delich. Source for the Wood script credit \"Spawn of Mars and Other Stories\" (2015).Cover story.Letterer credit by Craig Delich.",19
    );
    INSERT INTO story(id, title, issue_id, synopsis, reprint_notes, notes, type_id) VALUES(79903,"Prediction of Disaster!", 8670,"Professor Horton discovers that the Beta Orion is going nova and about to explode... and does. Then he makes a startling discovery: one can determine when a star is about to explode by noting an excess of infra-red rays. He successfully proves that theory then accidentally discovers that the Earth's sun is about to go nova and seeks to warn the world.",NULL,NULL,19
